// frontend/src/App.js
import React, { useEffect, useRef, useState } from "react";
import "./App.css";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:8000/api/chat";

function extractImageUrls(text) {
  const urls = [];
  // Markdown image ![alt](url)
  const mdImg = /\!\[[^\]]*\]\((https?:\/\/[^\s)]+)\)/g;
  let m;
  while ((m = mdImg.exec(text))) urls.push(m[1]);
  // Plain URLs ending in image ext
  const urlRegex = /(https?:\/\/[^\s)]+\.(?:png|jpg|jpeg|gif|webp))/g;
  while ((m = urlRegex.exec(text))) urls.push(m[1]);
  return Array.from(new Set(urls));
}

function App() {
  const [messages, setMessages] = useState([
    { role: "assistant", content: "Hi! Upload or paste an itinerary, then ask anything about it. Images included in itinerary or in answers will render automatically." }
  ]);
  const [input, setInput] = useState("");
  const [itineraryText, setItineraryText] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const endRef = useRef(null);

  useEffect(() => endRef.current?.scrollIntoView({ behavior: "smooth" }), [messages, loading]);

  const handleFile = async (e) => {
    const f = e.target.files?.[0];
    if (!f) return;
    const text = await f.text();
    setItineraryText(text);
  };

  const sendMessage = async (e) => {
    e?.preventDefault();
    if (!input.trim()) return;
    setError("");
    const userMsg = { role: "user", content: input.trim() };
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setLoading(true);
    try {
      const res = await fetch(BACKEND_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          question: userMsg.content,
          history: messages.map(m => ({ role: m.role, content: m.content })),
          itinerary_content: itineraryText || undefined
        }),
      });
      if (!res.ok) {
        const txt = await res.text();
        throw new Error(txt || "Request failed");
      }
      const data = await res.json();
      const answer = data.answer || data?.body?.answer || JSON.stringify(data);
      setMessages(prev => [...prev, { role: "assistant", content: answer }]);
    } catch (err) {
      console.error(err);
      setError(typeof err === "string" ? err : err.message || "Something went wrong");
      setMessages(prev => [...prev, { role: "assistant", content: "Could not reach server. Check console." }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>Itinerary Chatbot</h1>
        <p>Upload/paste an itinerary (JSON or text). Ask questions based on that itinerary only.</p>
      </header>

      <main className="chat-shell">
        <section className="controls">
          <label className="file-label">
            Upload itinerary file (JSON/plain text):
            <input type="file" accept=".json,.txt" onChange={handleFile} />
          </label>

          <label className="paste-label">
            Or paste itinerary text:
            <textarea value={itineraryText} onChange={(e) => setItineraryText(e.target.value)} placeholder="Paste itinerary or JSON here" rows={4} />
          </label>
        </section>

        <div className="chat-window">
          {messages.map((m, i) => {
            const imgs = extractImageUrls(m.content || "");
            return (
              <div key={i} className={`chat-row ${m.role}`}>
                <div className="avatar">{m.role === "user" ? "You" : "Bot"}</div>
                <div className="bubble">
                  <div dangerouslySetInnerHTML={{ __html: m.content.replace(/\n/g, "<br/>") }} />
                  {imgs.length > 0 && (
                    <div className="images">
                      {imgs.map((u, idx) => <img key={idx} src={u} alt="itinerary" onError={(e)=>e.target.remove()} />)}
                    </div>
                  )}
                </div>
              </div>
            );
          })}
          {loading && <div className="chat-row assistant"><div className="avatar">Bot</div><div className="bubble">Thinking...</div></div>}
          <div ref={endRef} />
        </div>

        <form className="chat-input-row" onSubmit={sendMessage}>
          <input value={input} onChange={(e)=>setInput(e.target.value)} placeholder="Ask something like: 'What happens on Day 3?'" />
          <button type="submit" disabled={loading || !input.trim()}>{loading ? "Sending..." : "Send"}</button>
        </form>

        {error && <div className="error">{error}</div>}
      </main>
    </div>
  );
}

export default App;
