// src/App.js  (replace existing)
import React, { useState, useEffect, useRef } from "react";
import "./App.css";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "/api";

function formatTime() {
  return new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });
}

function App() {
  const [messages, setMessages] = useState([
    { role: "assistant", content: "Hey — ask me anything about the pre-loaded 5-night Dubai plan. (Tip: try 'What happens on Day 3?')", time: formatTime() },
  ]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, loading]);

  const sendMessage = async (e) => {
    e?.preventDefault();
    if (!input.trim() || loading) return;
    setError("");
    const userMsg = { role: "user", content: input.trim(), time: formatTime() };
    const historyForBackend = messages.map(m => ({ role: m.role, content: m.content }));
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setLoading(true);

    try {
      const res = await fetch(`${BACKEND_URL}/chat`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ question: userMsg.content, history: historyForBackend }),
      });
      if (!res.ok) {
        const errText = await res.text();
        throw new Error(errText || "Chat request failed");
      }
      const data = await res.json();
      setMessages(prev => [...prev, { role: "assistant", content: data.answer, time: formatTime() }]);
    } catch (err) {
      console.error(err);
      setError(typeof err === "string" ? err : err.message || "Something went wrong");
      setMessages(prev => [...prev, { role: "assistant", content: "Sorry — couldn't reach the server. Try again.", time: formatTime() }]);
    } finally {
      setLoading(false);
    }
  };

  const quickExample = (q) => {
    setInput(q);
  };

  return (
    <div className="app" data-testid="app-root">
      <header className="app-header">
        <h1 data-testid="title">Dubai Itinerary Q&A</h1>
        <p data-testid="subtitle">Ask anything about the pre-loaded 5-night Dubai plan — fast answers, no guesswork.</p>
      </header>

      <main className="chat-shell" data-testid="chat-shell">
        <div className="chat-window" data-testid="chat-window" role="log" aria-live="polite">
          {messages.map((m, idx) => (
            <div key={idx} className={`chat-row ${m.role}`} data-testid={m.role === "user" ? "message-user" : "message-assistant"}>
              <div className="avatar">{m.role === "user" ? "You" : "Bot"}</div>
              <div className="bubble">
                <div style={{ fontSize: "0.85rem", marginBottom: "6px" }}>{m.content}</div>
                <div style={{ fontSize: "0.72rem", color: "#94a3b8", marginTop: "6px", textAlign: "right" }}>{m.time}</div>
              </div>
            </div>
          ))}
          {loading && (
            <div className="chat-row assistant" data-testid="message-loading">
              <div className="avatar">Bot</div>
              <div className="bubble">Thinking... ✨</div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        <form className="chat-input-row" onSubmit={sendMessage} data-testid="chat-form">
          <input
            data-testid="chat-input"
            type="text"
            placeholder="E.g. 'What is planned on Day 3?'"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            disabled={loading}
            aria-label="Ask about itinerary"
          />
          <button data-testid="chat-send-button" type="submit" disabled={loading || !input.trim()}>
            {loading ? "Sending..." : "Send"}
          </button>
        </form>

        <div style={{ display: "flex", gap: 8, marginTop: 10 }}>
          <button onClick={() => quickExample("What happens on Day 3?")} disabled={loading}>Example: Day 3</button>
          <button onClick={() => quickExample("Do Indians need a visa?")} disabled={loading}>Example: Visa</button>
        </div>

        {error && (
          <div className="error" data-testid="error-banner">
            {error}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
