import React, { useState, useEffect, useRef } from "react";
import "./App.css";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "/api";

function App() {
  const [messages, setMessages] = useState([
    { role: "assistant", content: "Hi! Ask me anything about your 5-night Dubai itinerary." },
  ]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [messages]);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim() || loading) return;
    setError("");

    const newUserMessage = { role: "user", content: input.trim() };
    const history = [...messages, newUserMessage];
    setMessages(history);
    setInput("");
    setLoading(true);

    try {
      const res = await fetch(`${BACKEND_URL}/chat`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          question: newUserMessage.content,
          history: history.map((m) => ({ role: m.role, content: m.content })),
        }),
      });

      if (!res.ok) {
        throw new Error("Chat request failed");
      }
      const data = await res.json();
      setMessages((prev) => [...prev, { role: "assistant", content: data.answer }]);
    } catch (err) {
      setError(err.message || "Something went wrong");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app" data-testid="app-root">
      <header className="app-header">
        <h1 data-testid="title">Dubai Itinerary Q&A</h1>
        <p data-testid="subtitle">Ask any question about the pre-loaded 5-night Dubai plan.</p>
      </header>

      <main className="chat-shell" data-testid="chat-shell">
        <div className="chat-window" data-testid="chat-window">
          {messages.map((m, idx) => (
            <div
              key={idx}
              className={`chat-row ${m.role}`}
              data-testid={m.role === "user" ? "message-user" : "message-assistant"}
            >
              <div className="avatar">{m.role === "user" ? "You" : "Bot"}</div>
              <div className="bubble">{m.content}</div>
            </div>
          ))}
          {loading && (
            <div className="chat-row assistant" data-testid="message-loading">
              <div className="avatar">Bot</div>
              <div className="bubble">Thinking...</div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        <form className="chat-input-row" onSubmit={sendMessage} data-testid="chat-form">
          <input
            data-testid="chat-input"
            type="text"
            placeholder="E.g. What is planned on Day 3?"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            disabled={loading}
          />
          <button
            data-testid="chat-send-button"
            type="submit"
            disabled={loading || !input.trim()}
          >
            {loading ? "Sending..." : "Send"}
          </button>
        </form>

        {error && (
          <div className="error" data-testid="error-banner">
            {error}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
