import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;

function App() {
  const [itineraries, setItineraries] = useState([]);
  const [selectedItinerary, setSelectedItinerary] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [messages, setMessages] = useState([]);
  const [question, setQuestion] = useState('');
  const [loading, setLoading] = useState(false);

  // Form state
  const [newItinerary, setNewItinerary] = useState({
    title: '',
    destination: '',
    duration: '',
    content: ''
  });

  useEffect(() => {
    fetchItineraries();
  }, []);

  const fetchItineraries = async () => {
    try {
      const response = await axios.get(`${BACKEND_URL}/api/itineraries`);
      setItineraries(response.data);
    } catch (error) {
      console.error('Error fetching itineraries:', error);
    }
  };

  const handleAddItinerary = async (e) => {
  e.preventDefault();
  try {
    const resp = await axios.post(`${BACKEND_URL}/api/itineraries`, newItinerary);
    const created = resp.data;
    // Optimistically add to list and select
    setItineraries(prev => [created, ...prev]);
    setNewItinerary({ title: '', destination: '', duration: '', content: '' });
    setShowAddForm(false);
  } catch (error) {
    console.error('Error adding itinerary:', error.response?.data || error.message);
    alert('Failed to add itinerary. Please try again.');
  }
};

  const handleSelectItinerary = async (itinerary) => {
    setSelectedItinerary(itinerary);
    setMessages([{
      role: 'assistant',
      content: `Hi! I'm your travel assistant. I have loaded the "${itinerary.title}" itinerary. Feel free to ask me any questions about it!`
    }]);
  };

  const handleAskQuestion = async (e) => {
    e.preventDefault();
    if (!question.trim() || !selectedItinerary) return;

    const userMessage = { role: 'user', content: question };
    setMessages(prev => [...prev, userMessage]);
    setQuestion('');
    setLoading(true);

    try {
      const response = await axios.post(`${BACKEND_URL}/api/ask`, {
  itinerary_id: selectedItinerary.id,
  question: question,
  history: messages.map(m => ({ role: m.role, content: m.content }))
});

      const assistantMessage = { role: 'assistant', content: response.data.answer };
      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      console.error('Error asking question:', error);
      const errorMessage = { 
        role: 'assistant', 
        content: 'Sorry, I encountered an error. Please try again.' 
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                TravelConcierge
              </h1>
              <p className="text-gray-600 mt-1">Your AI-Powered Travel Assistant</p>
            </div>
            <button
              onClick={() => setShowAddForm(!showAddForm)}
              className="px-6 py-3 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-lg font-semibold hover:shadow-lg transform hover:-translate-y-0.5 transition-all duration-200"
              data-testid="add-itinerary-btn"
            >
              + New Itinerary
            </button>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Add Itinerary Form */}
        {showAddForm && (
          <div className="bg-white rounded-2xl shadow-xl p-8 mb-8 border border-gray-200" data-testid="add-itinerary-form">
            <h2 className="text-2xl font-bold text-gray-800 mb-6">Add New Itinerary</h2>
            <form onSubmit={handleAddItinerary} className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Title *</label>
                  <input
                    type="text"
                    required
                    value={newItinerary.title}
                    onChange={(e) => setNewItinerary({...newItinerary, title: e.target.value})}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="e.g., 5-Night Dubai Adventure"
                    data-testid="itinerary-title-input"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Destination</label>
                  <input
                    type="text"
                    value={newItinerary.destination}
                    onChange={(e) => setNewItinerary({...newItinerary, destination: e.target.value})}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="e.g., Dubai, UAE"
                    data-testid="itinerary-destination-input"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Duration</label>
                  <input
                    type="text"
                    value={newItinerary.duration}
                    onChange={(e) => setNewItinerary({...newItinerary, duration: e.target.value})}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="e.g., 5 Nights / 6 Days"
                    data-testid="itinerary-duration-input"
                  />
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Itinerary Details *</label>
                <textarea
                  required
                  value={newItinerary.content}
                  onChange={(e) => setNewItinerary({...newItinerary, content: e.target.value})}
                  rows="10"
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent font-mono text-sm"
                  placeholder="Paste your itinerary details here (supports markdown formatting)..."
                  data-testid="itinerary-content-input"
                />
              </div>
              <div className="flex gap-4">
                <button
                  type="submit"
                  className="px-6 py-3 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-lg font-semibold hover:shadow-lg transform hover:-translate-y-0.5 transition-all duration-200"
                  data-testid="submit-itinerary-btn"
                >
                  Save Itinerary
                </button>
                <button
                  type="button"
                  onClick={() => setShowAddForm(false)}
                  className="px-6 py-3 bg-gray-200 text-gray-700 rounded-lg font-semibold hover:bg-gray-300 transition-colors"
                  data-testid="cancel-btn"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Itineraries List */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-2xl shadow-xl p-6 border border-gray-200">
              <h2 className="text-xl font-bold text-gray-800 mb-4">Your Itineraries</h2>
              <div className="space-y-3">
                {itineraries.length === 0 ? (
                  <p className="text-gray-500 text-center py-8">No itineraries yet. Add your first one!</p>
                ) : (
                  itineraries.map((itinerary) => (
                    <div
                      key={itinerary.id}
                      onClick={() => handleSelectItinerary(itinerary)}
                      className={`p-4 rounded-lg cursor-pointer transition-all duration-200 ${
                        selectedItinerary?.id === itinerary.id
                          ? 'bg-gradient-to-r from-blue-100 to-purple-100 border-2 border-blue-500'
                          : 'bg-gray-50 hover:bg-gray-100 border border-gray-200'
                      }`}
                      data-testid={`itinerary-item-${itinerary.id}`}
                    >
                      <h3 className="font-semibold text-gray-800">{itinerary.title}</h3>
                      {itinerary.destination && (
                        <p className="text-sm text-gray-600 mt-1">üìç {itinerary.destination}</p>
                      )}
                      {itinerary.duration && (
                        <p className="text-sm text-gray-600">‚è±Ô∏è {itinerary.duration}</p>
                      )}
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>

          {/* Chat Interface */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-2xl shadow-xl border border-gray-200 flex flex-col" style={{ height: '600px' }}>
              {selectedItinerary ? (
                <>
                  {/* Chat Header */}
                  <div className="p-6 border-b border-gray-200 bg-gradient-to-r from-blue-50 to-purple-50">
                    <h2 className="text-xl font-bold text-gray-800">{selectedItinerary.title}</h2>
                    <p className="text-sm text-gray-600 mt-1">Ask me anything about this itinerary!</p>
                  </div>

                  {/* Messages */}
                  <div className="flex-1 overflow-y-auto p-6 space-y-4" data-testid="chat-messages">
                    {messages.map((message, index) => (
                      <div
                        key={index}
                        className={`flex ${
                          message.role === 'user' ? 'justify-end' : 'justify-start'
                        }`}
                      >
                        <div
                          className={`max-w-[80%] p-4 rounded-2xl ${
                            message.role === 'user'
                              ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white'
                              : 'bg-gray-100 text-gray-800'
                          }`}
                        >
                          <p className="whitespace-pre-wrap">{message.content}</p>
                        </div>
                      </div>
                    ))}
                    {loading && (
                      <div className="flex justify-start">
                        <div className="bg-gray-100 p-4 rounded-2xl">
                          <div className="flex space-x-2">
                            <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                            <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                            <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                          </div>
                        </div>
                      </div>
                    )}
                  </div>

                  {/* Input Form */}
                  <form onSubmit={handleAskQuestion} className="p-6 border-t border-gray-200">
                    <div className="flex gap-3">
                      <input
                        type="text"
                        value={question}
                        onChange={(e) => setQuestion(e.target.value)}
                        placeholder="Ask about your itinerary..."
                        className="flex-1 px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                        disabled={loading}
                        data-testid="question-input"
                      />
                      <button
                        type="submit"
                        disabled={loading || !question.trim()}
                        className="px-6 py-3 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-lg font-semibold hover:shadow-lg transform hover:-translate-y-0.5 transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
                        data-testid="send-question-btn"
                      >
                        Send
                      </button>
                    </div>
                  </form>
                </>
              ) : (
                <div className="flex-1 flex items-center justify-center p-6" data-testid="no-itinerary-selected">
                  <div className="text-center">
                    <div className="text-6xl mb-4">‚úàÔ∏è</div>
                    <h3 className="text-xl font-semibold text-gray-800 mb-2">Select an Itinerary</h3>
                    <p className="text-gray-600">Choose an itinerary from the list to start asking questions</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
