import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

// âœ… FIX: Get backend URL with validation
const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;

if (!BACKEND_URL) {
  console.warn('âš ï¸ REACT_APP_BACKEND_URL not set. Set it in .env.local');
}

function App() {
  const [itineraries, setItineraries] = useState([]);
  const [selectedItinerary, setSelectedItinerary] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [messages, setMessages] = useState([]);
  const [question, setQuestion] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Form state
  const [newItinerary, setNewItinerary] = useState({
    title: '',
    destination: '',
    duration: '',
    content: ''
  });

  // âœ… FIX: Fetch itineraries on mount
  useEffect(() => {
    fetchItineraries();
  }, []);

  // âœ… FIX: Fetch itineraries with error handling
  const fetchItineraries = async () => {
    try {
      setError(null);
      const response = await axios.get(`${BACKEND_URL}/api/itineraries`);
      setItineraries(response.data);
    } catch (error) {
      const message = `Error fetching itineraries: ${error.message}`;
      console.error(message);
      setError(message);
    }
  };

  // âœ… FIX: Add itinerary with better error handling
  const handleAddItinerary = async (e) => {
    e.preventDefault();

    // Validation
    if (!newItinerary.title.trim() || !newItinerary.content.trim()) {
      setError('Title and content are required');
      return;
    }

    try {
      setError(null);
      const resp = await axios.post(`${BACKEND_URL}/api/itineraries`, newItinerary);
      const created = resp.data;

      // Optimistically add to list and select
      setItineraries(prev => [created, ...prev]);
      setSelectedItinerary(created);
      setMessages([{
        role: 'assistant',
        content: `Hi! I'm your travel assistant. I have loaded the "${created.title}" itinerary. Feel free to ask me any questions about it!`
      }]);

      setNewItinerary({ title: '', destination: '', duration: '', content: '' });
      setShowAddForm(false);
    } catch (error) {
      const message = `Failed to add itinerary: ${error.response?.data?.detail || error.message}`;
      console.error(message);
      setError(message);
    }
  };

  // âœ… FIX: Select itinerary
  const handleSelectItinerary = (itinerary) => {
    setSelectedItinerary(itinerary);
    setMessages([{
      role: 'assistant',
      content: `Hi! I'm your travel assistant. I have loaded the "${itinerary.title}" itinerary. Feel free to ask me any questions about it!`
    }]);
    setError(null);
  };

  // âœ… FIX: Ask question with proper error handling
  const handleAskQuestion = async (e) => {
    e.preventDefault();

    if (!question.trim() || !selectedItinerary) return;

    const userMessage = { role: 'user', content: question };
    setMessages(prev => [...prev, userMessage]);
    setQuestion('');
    setLoading(true);
    setError(null);

    try {
      // âœ… FIX: Limit history to last 10 messages to avoid token overflow
      const recentMessages = messages.slice(-10);

      const response = await axios.post(`${BACKEND_URL}/api/ask`, {
        itinerary_id: selectedItinerary.id,
        question: question,
        history: recentMessages.map(m => ({
          role: m.role,
          content: m.content
        }))
      });

      // âœ… FIX: Validate response structure
      if (response.data && response.data.answer) {
        const assistantMessage = {
          role: 'assistant',
          content: response.data.answer
        };
        setMessages(prev => [...prev, assistantMessage]);
      } else {
        throw new Error('Unexpected response format from server');
      }
    } catch (error) {
      console.error('Error asking question:', error);
      const errorMessage = {
        role: 'assistant',
        content: `Sorry, I encountered an error: ${error.response?.data?.detail || error.message || 'Unknown error'}`
      };
      setMessages(prev => [...prev, errorMessage]);
      setError(error.response?.data?.detail || error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app">
      {/* âœ… FIX: Error display */}
      {error && (
        <div className="error-banner">
          <span>{error}</span>
          <button onClick={() => setError(null)}>âœ•</button>
        </div>
      )}

      <div className="container">
        {/* Left sidebar - Itineraries list */}
        <div className="sidebar">
          <div className="sidebar-header">
            <h1>ðŸ—ºï¸ Travel Itineraries</h1>
            <button
              className="btn-add"
              onClick={() => setShowAddForm(!showAddForm)}
            >
              + Add
            </button>
          </div>

          {/* âœ… FIX: Add itinerary form */}
          {showAddForm && (
            <form onSubmit={handleAddItinerary} className="add-form">
              <input
                type="text"
                placeholder="Title"
                value={newItinerary.title}
                onChange={(e) =>
                  setNewItinerary({ ...newItinerary, title: e.target.value })
                }
                required
              />
              <input
                type="text"
                placeholder="Destination"
                value={newItinerary.destination}
                onChange={(e) =>
                  setNewItinerary({
                    ...newItinerary,
                    destination: e.target.value
                  })
                }
              />
              <input
                type="text"
                placeholder="Duration"
                value={newItinerary.duration}
                onChange={(e) =>
                  setNewItinerary({ ...newItinerary, duration: e.target.value })
                }
              />
              <textarea
                placeholder="Itinerary content..."
                value={newItinerary.content}
                onChange={(e) =>
                  setNewItinerary({ ...newItinerary, content: e.target.value })
                }
                required
              ></textarea>
              <button type="submit" className="btn-primary">
                Save
              </button>
              <button
                type="button"
                className="btn-secondary"
                onClick={() => setShowAddForm(false)}
              >
                Cancel
              </button>
            </form>
          )}

          {/* âœ… FIX: Itineraries list */}
          <div className="itineraries-list">
            {itineraries.length === 0 ? (
              <p className="empty-state">No itineraries yet. Add your first one!</p>
            ) : (
              itineraries.map((itinerary) => (
                <div
                  key={itinerary.id}
                  className={`itinerary-item ${
                    selectedItinerary?.id === itinerary.id ? 'active' : ''
                  }`}
                  onClick={() => handleSelectItinerary(itinerary)}
                >
                  <h3>{itinerary.title}</h3>
                  {itinerary.destination && (
                    <p className="destination">ðŸ“ {itinerary.destination}</p>
                  )}
                  {itinerary.duration && (
                    <p className="duration">â±ï¸ {itinerary.duration}</p>
                  )}
                </div>
              ))
            )}
          </div>
        </div>

        {/* Right section - Chat interface */}
        <div className="chat-section">
          {!selectedItinerary ? (
            <div className="no-selection">
              <h2>Select an itinerary to start chatting</h2>
              <p>Choose from the list on the left or create a new one</p>
            </div>
          ) : (
            <>
              {/* Chat header */}
              <div className="chat-header">
                <div>
                  <h2>{selectedItinerary.title}</h2>
                  {selectedItinerary.destination && (
                    <p>ðŸ“ {selectedItinerary.destination}</p>
                  )}
                </div>
              </div>

              {/* âœ… FIX: Messages display */}
              <div className="messages-container">
                {messages.map((msg, idx) => (
                  <div key={idx} className={`message message-${msg.role}`}>
                    <div className="message-content">
                      {msg.role === 'user' ? 'ðŸ‘¤ You' : 'ðŸ¤– Assistant'}
                    </div>
                    <p>{msg.content}</p>
                  </div>
                ))}
                {loading && (
                  <div className="message message-assistant loading">
                    <div className="spinner"></div>
                    <p>Thinking...</p>
                  </div>
                )}
              </div>

              {/* âœ… FIX: Chat input form */}
              <form onSubmit={handleAskQuestion} className="chat-form">
                <input
                  type="text"
                  placeholder="Ask me anything about this itinerary..."
                  value={question}
                  onChange={(e) => setQuestion(e.target.value)}
                  disabled={loading}
                />
                <button
                  type="submit"
                  disabled={!question.trim() || loading}
                  className="btn-send"
                >
                  {loading ? 'â³' : 'âž¤'}
                </button>
              </form>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
