import React, { useState } from "react";
import "./App.css";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;

function App() {
  const [itinerary, setItinerary] = useState("");
  const [messages, setMessages] = useState([]);
  const [question, setQuestion] = useState("");
  const [loading, setLoading] = useState(false);

  const sendMessage = async () => {
    if (!question.trim()) return;

    const newUserMsg = { role: "user", content: question };

    setMessages(prev => [...prev, newUserMsg]);
    setQuestion("");
    setLoading(true);

    try {
      const res = await fetch(BACKEND_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          question,
          history: messages,
          itinerary_content: itinerary
        })
      });

      const data = await res.json();

      const botMsg = {
        role: "assistant",
        content: data.answer || "Error fetching answer."
      };

      setMessages(prev => [...prev, botMsg]);
    } catch (e) {
      setMessages(prev => [...prev, {
        role: "assistant",
        content: "Server error. Check console."
      }]);
    }

    setLoading(false);
  };

  return (
    <div className="app-container">

      <div className="header">
        <h1>Trip Concierge</h1>
        <p>Your personal AI travel assistant</p>
      </div>

      {/* ITINERARY INPUT */}
      <div className="box">
        <textarea
          className="textarea"
          rows={6}
          placeholder="Paste itinerary text or JSON here..."
          value={itinerary}
          onChange={(e) => setItinerary(e.target.value)}
        />
      </div>

      {/* CHAT WINDOW */}
      <div className="box chat-window">
        {messages.map((msg, i) => (
          <div key={i} className={`msg ${msg.role}`}>
            <div className="bubble">{msg.content}</div>
          </div>
        ))}

        {loading && (
          <div className="msg bot">
            <div className="bubble">Thinking...</div>
          </div>
        )}
      </div>

      {/* SEND MESSAGE */}
      <div className="send-row">
        <input
          className="send-input"
          placeholder="Ask something like: ‘What happens on day 3?’"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
        />
        <button className="send-btn" onClick={sendMessage}>
          Send
        </button>
      </div>

    </div>
  );
}

export default App;
